import astimport oslinear_dic = {"a": 4, "b": 5}def read_file_linear():    if not os.path.exists("linear.txt"):        with open("linear.txt", "w", encoding="utf-8") as file:            file.write(str(linear_dic))    else:        with open("linear.txt", "r") as f:            content = f.read()            try:                dic = ast.literal_eval(content)  # Перетворюємо рядок у словник                if isinstance(dic, dict):  # Переконуємось, що це словник                    for key, value in dic.items():                        print(key, value)            except (SyntaxError, ValueError):                print("Помилка: неможливо перетворити вміст файлу у словник.")read_file_linear()