import numpy as npfrom scipy.stats import binom# Параметриn = 10  # кількість випробуваньeta0 = 0.3  # ймовірність при H0eta1 = 0.6  # ймовірність при H1# Параметри НП-критеріюP = 0.5  # апріорна ймовірність H1C1 = 1  # ціна помилки 1-го родуC2 = 1  # ціна помилки 2-го родуthreshold = (P * C1) / ((1 - P) * C2)  # Поріг НП-критерію# Функція обчислення відношення правдоподібностіdef likelihood_ratio(y, n, eta0, eta1):    return (eta1 / eta0) ** y * ((1 - eta1) / (1 - eta0)) ** (n - y)# Визначення критичної областіcritical_region = []for y in range(n + 1):    ratio = likelihood_ratio(y, n, eta0, eta1)    if ratio >= threshold:        critical_region.append(y)print(f"Поріг λ(y) ≥ {threshold:.3f}")print(f"Критична область (відхиляємо H0): y ∈ {critical_region}")# Обчислення ймовірностей помилокalpha = sum(binom.pmf(y, n, eta0) for y in critical_region)beta = sum(binom.pmf(y, n, eta1) for y in range(n + 1) if y not in critical_region)print(f"Ймовірність помилки 1-го роду α = {alpha:.4f}")print(f"Ймовірність помилки 2-го роду β = {beta:.4f}")